#dp solution was too slow
class Solution(object):
    
    def longestPalindrome(self, s):
        l=list(s)
        max_str=s[0]
        for i in range(0, len(l)-1):
            odd =self.getLongest(l, i, i)#odd
            even= self.getLongest(l, i, i+1) #even
            if len(odd)>len(max_str):
                max_str=odd
            if len(even)>len(max_str):
                max_str=even
        return ''.join(max_str)
       
    def getLongest(self, l, start, end):
        
        if not l[start]==l[end]:
            return list(l[start])
        while start>0 and end<len(l)-1:
            start-=1
            end+=1
            #print l[start:end+1]
            if not l[start]==l[end]:
                return l[start+1:end]  
        return l[start:end+1]
    
        
        
    
